pragma solidity >=0.5.0 <=0.8.14;

contract CookingBook{
    uint public recipeCount = 0;

    struct Recipe{
        uint id;
        string content;
        bool completed;
    }

    mapping(uint => Recipe) public recipes;

    constructor() public {
        createBook("Water");
    }
    function createBook(string memory _content) public {
        recipeCount++;
        recipes[recipeCount] = Recipe(recipeCount, _content, false);
    }
    function hello() pure public {
        
    }

    
}
